(2300, 640, '_form', '<div class=\"contact-form\">\n                           <div class=\"row\">\n\n                               <div class=\"col-12 col-md-12\">\n                                   <div class=\"form-group\">\n  [text* name-170 id:exampleInputEmail1 class:form-control placeholder \"Name of the candidate (In English - as in Matriculation Certificate)\"]\n\n                                   </div>\n                               </div>\n                               <div class=\"col-12 col-md-12\">\n                                   <div class=\"form-group\">\n                                       [text* fname-170 id:exampleInputEmail1 class:form-control placeholder \"Father\'s Name\"]\n                                   </div>\n                               </div>\n\n  <div class=\"col-12 col-md-12\">\n                                   <div class=\"form-group\">\n                                       [text* mname-170 id:exampleInputEmail1 class:form-control placeholder \"Mother\'s Name\"]\n                                   </div>\n                               </div>\n\n  <div class=\"col-12 col-md-12\">\n                                   <div class=\"form-group\">\n                                       [text foccu-170 id:exampleInputEmail1 class:form-control placeholder \"Father\'s Occupation\"]\n                                   </div>\n                               </div>\n\n  <div class=\"col-12 col-md-12\">\n                                   <div class=\"form-group\">\n                                      [tel tel-66 id:exampleInputEmail1 class:form-control placeholder \"Mobile Number\"]\n                                   </div>\n                               </div>\n\n  <div class=\"col-12 col-md-6\">\n                                  <div class=\"form-group\">\n                                      [textarea* pmaddres-170 x1 id:exampleInputEmail1 class:form-control placeholder \"Permanent Address\"]\n                                  </div>\n                              </div>\n  <div class=\"col-12 col-md-6\">\n                                  <div class=\"form-group\">\n                                      [text* pin2-170 id:exampleInputEmail1 class:form-control placeholder \"Pin Code\"]\n                                  </div>\n                              </div>\n  <div class=\"col-12 col-md-6\">\n                                   <div class=\"form-group\">\n                                       [tel* phonwstd-170 id:exampleInputEmail1 class:form-control placeholder \"Phone number with std code\"]\n                                   </div>\n                               </div>\n  <div class=\"col-12 col-md-6\">\n                                  <div class=\"form-group\">\n                                      [tel* mob-170 id:exampleInputEmail1 class:form-control placeholder \"Mobile(Student)\"]\n                                  </div>\n                              </div>\n\n  <div class=\"col-12 col-md-6\">\n      <div class=\"form-group\">\n            [email* email2-427 id:exampleInputEmail1 class:form-control placeholder \"Email Address\"]\n\n      </div>\n  </div>\n  <div class=\"col-12 col-md-6\">\n      <div class=\"form-group\">\n            [text* blood-427 id:exampleInputEmail1 class:form-control placeholder \"Blood Group\"]\n\n      </div>\n  </div>\n  <div class=\"col-12 col-md-4\">\n           <div class=\"form-group\">\n             <p class=\"dob-class\">Date Of Birth (Format -Date/Month/Year)</p>\n              [date* date-662 id:exampleInputEmail1 class:form-control placeholder \"Date Of Birth\"]\n           </div>\n       </div>\n<div class=\"col-12 col-md-12\">\n        <h4 class=\"edu-class\">EDUCATIONAL QUALIFICATIONS:</h4>\n  <div class=\"row\">\n\n   <div class=\"col-12 col-md-12\">\n\n      <div class=\"row\">\n         <div class=\"col-12 col-md-3\">\n          <p class=\"dob-class\">SSLC or Its Equivalent:</p>\n          </div>\n\n          <div class=\"col-12 col-md-5\">\n            [text* yofp-170 x1 id:exampleInputEmail1 class:form-control placeholder \"Year of passing\"]\n          </div>\n\n          <div class=\"col-12 col-md-4\">\n            [text* percent-170 x1 id:exampleInputEmail1 class:form-control placeholder \"Percentage of Marks/ CGPA\"]\n          </div>\n\n          <div class=\"col-12 col-md-3\">\n           <p class=\"dob-class\">Plus Two or Its Equivalent:</p>\n           </div>\n\n           <div class=\"col-12 col-md-5\">\n             [text* yofpplus-170 x1 id:exampleInputEmail1 class:form-control placeholder \"Year of passing\"]\n           </div>\n\n           <div class=\"col-12 col-md-4\">\n             [text* percentplus-170 x1 id:exampleInputEmail1 class:form-control placeholder \"Percentage of Marks/ CGPA\"]\n           </div>\n\n           <div class=\"col-12 col-md-3\">\n            <p class=\"dob-class\">Bachelor Degree:</p>\n            </div>\n\n            <div class=\"col-12 col-md-5\">\n              [text* yofpdegree-170 x1 id:exampleInputEmail1 class:form-control placeholder \"Year of passing\"]\n            </div>\n\n            <div class=\"col-12 col-md-4\">\n              [text* percentdegree-170 x1 id:exampleInputEmail1 class:form-control placeholder \"Percentage of Marks/ CGPA\"]\n            </div>\n\n            <div class=\"col-12 col-md-3\">\n             <p class=\"dob-class\">Post Graduate Degree (if any):</p>\n             </div>\n\n             <div class=\"col-12 col-md-5\">\n               [text yofppg-170 x1 id:exampleInputEmail1 class:form-control placeholder \"Year of passing\"]\n             </div>\n\n             <div class=\"col-12 col-md-4\">\n               [text percentpg-170 x1 id:exampleInputEmail1 class:form-control placeholder \"Percentage of Marks/ CGPA\"]\n             </div>\n\n             <div class=\"col-12 col-md-3\">\n              <p class=\"dob-class\">Other Degree/Diploma (If any):</p>\n              </div>\n\n              <div class=\"col-12 col-md-5\">\n                [text yofpoth-170 x1 id:exampleInputEmail1 class:form-control placeholder \"Year of passing\"]\n              </div>\n\n              <div class=\"col-12 col-md-4\">\n                [text percentoth-170 x1 id:exampleInputEmail1 class:form-control placeholder \"Percentage of Marks/ CGPA\"]\n              </div>\n\n          \n       </div>\n   </div>\n\n\n  </div>\n</div>\n\n       <div class=\"bottom-line\"></div>\n<div class=\"col-12 col-md-12\">\n           <h5 class=\"gen-class\">General Information (P) Mark only the relevent column</h5>\n         </div>\n\n  <div class=\"col-12 col-md-4\">\n    <div class=\"row\">\n      <div class=\"col-md-2\">\n         <p class=\"sex-class\">Sex</p>\n      </div>\n        <div class=\"col-md-10\">\n\n<div class=\"form-group\">\n                 [radio sex-469 id:exampleInputEmail1 class:form-control default:1 \"Male\" \"Female\"]\n </div>\n         </div>\n       </div>\n   </div>\n\n\n<div class=\"col-12 col-md-4\">\n   <div class=\"form-group\">\n    [text* religion-427 id:exampleInputEmail1 class:form-control placeholder \"Religion\"]\n\n   </div>\n</div>\n<div class=\"col-12 col-md-4\">\n   <div class=\"form-group\">\n    [text caste-427 id:exampleInputEmail1 class:form-control placeholder \"Caste\"]\n\n   </div>\n</div>\n <div class=\"col-12 col-md-6\">\n  <div class=\"row\">\n    <div class=\"col-12 col-md-2\">\n       <p class=\"sex-class\">Category</p>\n    </div>\n    <div class=\"col-12 col-md-10\">\n       <div class=\"form-group\">\n           [checkbox category-471 id:exampleInputEmail1 class:form-control \"GENERAL\" \"SC\" \"ST\" \"OBC\"]\n       </div>\n    </div>\n    </div>\n\n </div>\n\n\n <div class=\"col-12 col-md-6\">\n\n   <div class=\"row\">\n     <div class=\"col-12 col-md-6\">\n        <p class=\"sex-class\">Whether Hostel Facility Required ?</p>\n     </div>\n     <div class=\"col-12 col-md-6\">\n        <div class=\"form-group\">\n             [radio hostel-469 id:exampleInputEmail1 class:form-control default:1 \"Yes\" \"No\"]\n        </div>\n     </div>\n     </div>\n </div>\n\n <div class=\"col-12 col-md-6\">\n\n        <div class=\"form-group\">\n              [text exper-170 id:exampleInputEmail1 class:form-control placeholder \"Work Experience ,if any (in months)\"]\n        </div>\n </div>\n\n<div class=\"col-12 col-md-6\">\n\n        <div class=\"form-group\">\n              [text institute-170 id:exampleInputEmail1 class:form-control placeholder \"Institution last worked for\"]\n        </div>\n </div>\n <div class=\"col-12 col-md-12\">\n\n        <div class=\"form-group\">\n              [text echive-170 id:exampleInputEmail1 class:form-control placeholder \"Achievements/Awards\"]\n        </div>\n </div>\n\n <div class=\"col-12 col-md-3\">\n\n        <div class=\"form-group\">\n          <p>K-MAT/CAT/ MAT/CMAT Details:</p>\n        </div>\n </div>\n <div class=\"col-12 col-md-3\">\n\n        <div class=\"form-group\">\n          [text score-170 id:exampleInputEmail1 class:form-control placeholder \"Score\"]\n        </div>\n </div>\n  <div class=\"col-12 col-md-6\">\n\n        <div class=\"form-group\">\n          [text percent-170 id:exampleInputEmail1 class:form-control placeholder \"Percentage\"]\n        </div>\n </div>\n\n\n                           </div>\n\n                           <button type=\"submit\" class=\"submit-btn\">Submit <i class=\"fas fa-arrow-right\"></i></button>\n                           <div class=\"clearfix\"></div>\n                       </div>'),
