(5900, 1182, 'fee_table', '<table class=\"table table-responsive\">\r\n<thead>\r\n<tr>\r\n<th>Particulars</th>\r\n<th>Amount (Rs.)</th>\r\n</tr>\r\n</thead>\r\n<tbody>\r\n<tr>\r\n<td>Tuition Fee</td>\r\n<td>1,50,000 per year (Payable in 2 installments of Rs.75,000 each)</td>\r\n</tr>\r\n<tr>\r\n<td>Caution Deposit</td>\r\n<td>5,000</td>\r\n</tr>\r\n<tr>\r\n<td>Examination Fee</td>\r\n<td>1,700 per Trimester\r\n(subject to the decision of the university)</td>\r\n</tr>\r\n<tr>\r\n<td>Admission Fee</td>\r\n<td>500</td>\r\n</tr>\r\n<tr>\r\n<td>University Student Administration Fee</td>\r\n<td>1,000</td>\r\n</tr>\r\n<tr>\r\n<td>PTA Fund</td>\r\n<td>500</td>\r\n</tr>\r\n<tr>\r\n<td>Student Union &amp; Association Fees</td>\r\n<td>400 (Subject to change as per the decision of Students Union)</td>\r\n</tr>\r\n</tbody>\r\n</table>'),
(5901, 1182, '_fee_table', 'field_5c80bca575e53'),
(5902, 1182, 'note', '<ul class=\"list-unstyled list-arw\">\r\n 	<li>If the candidate is opting for bank loan, already collected tuition fee of Rs.25,000 will be reimbursed to the candidate as an when the bank loan is approved.</li>\r\n 	<li>Any candidate who desires to discontinue from the programme after confirmation of admission &amp; registration for the courses specified in the admit offer letter will forfeit the total amount of fees paid.</li>\r\n</ul>'),
